class ToggleLike{constructor(t){this.toggler=t,this.toggleLike()}toggleLike(){$(this.toggler).click((function(t){t.preventDefault();let e=this;$(e).find(".btn").find("i").toggleClass("blue"),$.ajax({type:"POST",url:$(e).attr("href")}).done((function(t){let n=parseInt($(e).attr("data-likes"));console.log(n),1==t.data.deleted?n-=1:n+=1,$(e).attr("data-likes",n),$(e).html(`\n          <button class="btn"><i class="fa fa-thumbs-up fa-lg" aria-hidden="true"></i></button>\n          <span>\n          ${n}\n          </span>\n          `)})).fail((function(t){console.log("error in completing the request"+t)}))}))}}